import { Component, OnInit } from '@angular/core';
import {HeaderService} from "../../services/header.service";
import {map, Observable} from "rxjs";
import {UserVo} from "../../models/userVo.model";

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.scss']
})
export class HeaderComponent implements OnInit {
  user$: Observable<UserVo> = this.headerService.getCurrentUser();
  private userFullName: Observable<string> =this.user$.pipe(
      map(user => user.fullName));
  constructor(private headerService: HeaderService) { }

  ngOnInit(): void {
  }

  logout() {
    this.headerService.loggoutUser(this.user$);
  }
}
<div>
<div class="container d-flex justify-content-between hdr">
  <a routerLink="" class="h1 text-decoration-none">
    <img class="logo-header" src="assets/images/svg/logo-no-background_new.svg">
  </a>
  <div class="nav">

    <nav class="navbar navbar-expand-lg">
      <div class="container-fluid">
        <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
          <span class="navbar-toggler-icon"></span>
        </button>
        <div class="collapse navbar-collapse" id="navbarNav">
          <ul class="navbar-nav">
            <li class="nav-item">
              <a routerLink="" class="nav-link active" aria-current="page" href="#">Home</a>
            </li>
            <ng-container *ngIf="user$ | async as user">
              <li class="nav-item">
                <p class="nav-link">Hello {{ user.fullName }}</p>
              </li>
              <li class="nav-item">
                <button type="button" (click)="logout()"  class="nav-link">Logout</button>
              </li>
            </ng-container>
            <ng-template #noUser>
            <li class="nav-item">
              <a routerLink="login" class="nav-link">Login</a>
            </li>
            <li class="nav-item">
              <a routerLink="register" class="nav-link" >Register</a>
            </li>
            </ng-template>
          </ul>
        </div>
      </div>
    </nav>
  </div>
</div>
</div>
